ALTER TABLE CUST.PROMO_CUST_STRATEGY ADD (DELIVERY_DAY_TYPE varchar2(10 byte));
UPDATE CUST.PROMO_CUST_STRATEGY PCS SET PCS.DELIVERY_DAY_TYPE='R' where PCS.PROMOTION_ID in(SELECT ID from CUST.PROMOTION_NEW WHERE CODE like 'WS_%');--Window Steering promotions
UPDATE CUST.PROMO_CUST_STRATEGY PCS SET PCS.DELIVERY_DAY_TYPE='A' where PCS.PROMOTION_ID in(SELECT ID from CUST.PROMOTION_NEW WHERE CODE not like 'WS_%');--Non window steering promotions.